function measure() {
    echo $(du -s -b . | awk '{print $1}')
}

BEFORE_CLEANUP=$(measure)

for dir in cmake-build-*; do 
    cmake --build $dir -- clean
done;

# Generated by Visual Studio Code
rm build/ -rf
rm rust/target -rf

AFTER_CLEANUP=$(measure)
DELTA=$(( (BEFORE_CLEANUP - AFTER_CLEANUP) / 1024 ))

echo "Cleaned $DELTA kB of disk space"
